package main;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 4 tipos de datos
		 * Enteros
		 * Decimales
		 * Caracteres
		 * Booleanos
		 */
		
		int version;
		version = 9;
		version = 10;
		
		System.out.println(version);
		
		int versionJava = 10;
		
		System.out.println(versionJava);
		
		float versionEspecifica = 9.8f; //flotantes deben terminar con el sufijo f
		
		char letra = 'a';
		char numero = 97; //hace referencia a a
		
		System.out.println(letra);
		System.out.println(numero); 
		
		boolean valor1 = true;
		boolean valor2 = false;
		
		// int variable1;
		// int variable2;
		// int variable3;
		
		// variable1 = 1;
		// variable2 = 2;
		// variable3 = 3;
		
		int variable1 = 1, variable2 = 2, variable3 = 3; //esto solo se puede hacer cuando son del mismo tipo
		float pi = 3.1415f, e = 0.2021f;
		
		
		System.out.println(variable1);
		System.out.println(variable2);
		System.out.println(variable3);
		
		final float pi2;
		pi2 = 3.1415f; 
		
		System.out.println(pi);
		
	}

}
